import java.util.*;
public class Sum_of_ArrayElement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] arr= {20,30,50};
		int sum=0;
		for(int i=0;i<arr.length;i++)
		{
			sum+=arr[i];
		}
		System.out.println("The sum of the elements= "+sum);
		
	}

}
